mapper.update:
UPDATE tblBusiness
SET b_email = #{b_email},
    b_tel = #{b_tel}
WHERE b_id = #{b_id}

UPDATE tblBdetail
SET bd_address = #{bd_address},
    bd_address_detail = #{bd_address_detail}
WHERE b_id = #{b_id}

UPDATE tblBusiness
SET b_pw = #{b_pw}
WHERE b_id = #{b_id}

UPDATE tblEReview
SET er_content = #{er_content},
	er_rate = #{er_rate}
WHERE o_seq = #{o_seq}

mapper.select:
SELECT *
FROM (
select s.*,  (select count(*) from tblSBComment where sb_seq = s.sb_seq) as count, ROWNUM AS rn
	    from tblSpeakB s
	    where b_id = #{b_id}
        ORDER BY s.sb_seq
) 
WHERE rn BETWEEN #{start} AND #{end}
